import React, { useEffect, useState } from "react";
import "./userpage.css"

const UserPage = () => {
    const [users, setUsers] = useState([]);


    const callApi = async () => {
        const data = await fetch(`http://localhost:3000/users`);
        const users = await data.json();
        setUsers(users);
    };

    console.log("setUsers", setUsers);

    // const handleDelete = async (itemId) => {

    //     // gọi api xóa
    //     // await fetch(`/users/${itemId}`, { method: 'DELETE' })
    //     await fetch(`http://localhost:3000/users/${itemId}`, {
    //         method: 'DELETE'
    //     })

    //     setUsers(prev => prev.filter(item => item.id !== itemId))

    // }

    useEffect(() => {
        callApi();
    }, []);

    return (
        <div> UserPage
            {users?.map((item, index) => {
                return (
                    <div>
                        <table>
                            <tr key={item.id}>
                                <th>{item.id}</th>
                                <th>{item.email}</th>
                                <th>{item.userName}</th>
                                <th>{item.fullName}</th>
                                <th>{item.department}</th>
                                <th>{item.position}</th>
                                <button>Edit</button>
                                <button>DElete</button>
                            </tr>
                        </table>
                    </div>
                )
            })}



        </div>
    )
}

export default UserPage;
