import React, { useState } from 'react';
import './SignUp.css';

const SignUp = () => {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        confirmPassword: '',
        birthday: '',
    });

    const [errorMessage, setErrorMessage] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {

        const { fullname, email, password, confirmPassword, birthday } = formData;

        if (!fullname || !email || !password || !confirmPassword || !birthday) {
            setErrorMessage('Vui lòng nhập đủ thông tin.');
            return;
        }

        console.log("ErrorMessage", ErrorMessage)

        if (password !== confirmPassword) {
            alert('Passwords do not match!');
            return;
        }


        console.log("formData", formData)
        console.log("username", fullname)
        console.log("email", email)
        console.log("password", password)
        console.log("confirmPassword", confirmPassword)
        console.log("birthday", birthday)
        alert('success')




        setFormData({
            fullname: '',
            email: '',
            password: '',
            confirmPassword: '',
            birthday: '',
        });
    };

    return (
        <div className="signup">
            <form onSubmit={handleSubmit}>
                <h2>Register System</h2>
                {errorMessage && <p className="error-message">{errorMessage}</p>}
                <div className="input-group">
                    <label htmlFor="fullname">Fullname</label>
                    <input
                        type="text"
                        id="fullname"
                        name="fullname"
                        placeholder="enter fullname"
                        value={formData.fullname}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="input-group">
                    <label htmlFor="email">Email</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        placeholder="enter mail"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="input-group">
                    <label htmlFor="password">Password</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        placeholder="enter password"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="input-group">
                    <label htmlFor="confirmPassword">Confirm Password</label>
                    <input
                        type="password"
                        id="confirmPassword"
                        name="confirmPassword"
                        placeholder="enter password"
                        value={formData.confirmPassword}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="input-group">
                    <label htmlFor="birthday">Birthday</label>
                    <input
                        type="date"
                        id="birthday"
                        name="birthday"
                        value={formData.birthday}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className='home'>
                    <button type="submit">Register</button>
                    <a href='login'>Do you have account?</a>
                </div>

            </form>
        </div>
    );
};

export default SignUp;
