import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    color: '',
};

const colorSlice = createSlice({
    name: 'color',
    initialState,
    reducers: {
        increment: (state) => {
            state.color = state.color === '' ? '#00FF00' : '#FF0000';
        }
    },
});

export const { increment } = colorSlice.actions;

export default colorSlice.reducer;
