import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    counter: 0,
};

const counterSlice = createSlice({
    name: 'counter',
    initialState,
    reducers: {
        increment: (state, action) => {
            state.counter = state.counter + 1;
        },
    },
});

export const { increment } = counterSlice.action;

export default counterSlice.reducer;
