import React, { useEffect, useState } from "react";
import "./userpage.css"

const UserPage = () => {
    const [users, setUsers] = useState([]);

    const callApi = async () => {
        const data = await fetch("http://localhost:3000/users");
        const result = await data.json();
        setUsers(result);
    };

    console.log("setUsers", setUsers);
    useEffect(() => {
        callApi();
    }, [])
    console.log("setUsers", setUsers);

    return (
        <div>
            <table>
                <tr>
                    <th>ID</th>
                    <th>Email</th>
                    <th>userName</th>
                    <th>Full Name</th>
                    <th>Department</th>
                    <th>Position</th>
                    <th >Action</th>
                </tr>
                {users?.map((user) => {
                    return (
                        <tr>
                            <td>{user.id}</td>
                            <td>{user.email}</td>
                            <td>{user.userName}</td>
                            <td>{user.fullname}</td>
                            <td>{user.department}</td>
                            <td>{user.position}</td>
                            <td>
                                <button>Edit</button>
                            </td>
                            <td>
                                <button>
                                    Delete
                                </button>
                            </td>
                        </tr>
                    );
                })}
            </table>
        </div>
    );
}
export default UserPage;
