import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    color: '#FFFFFF',
};

const colorSlice = createSlice({
    name: 'color',
    initialState,
    reducers: {
        change: (state, action) => {
            state.color = state.color === '#FFFFFF' ? '#00FF00' : '#FF0000';
        }
    },
});

export const { increment } = colorSlice.actions;

export default colorSlice.reducer;
