import React, { useEffect, useState } from "react";
import "./userpage.css"

const UserPage = () => {
    const [user, setUser] = useState([]);


    useEffect(() => {
        async function fetchData() {
            const data = await fetch("http://localhost:3000/users")
            const result = await data.json()
            setUser(result)
        }
        fetchData()
    }, []);
    console.log('setUser', setUser)


    const handleDelete = async (itemId) => {

        // gọi api xóa
        // await fetch(`/users/${itemId}`, { method: 'DELETE' })
        await fetch(`http://localhost:3000/users/${itemId}`, {
            method: 'DELETE'
        })

        setUser(prev => prev.filter(item => item.id !== itemId))

    }
    return (
        <div> UserPage
            {setUser?.data?.map((item, index) => {

                return (
                    <div>
                        <table>
                            <tr key={item.id}>
                                <th>{item.id}</th>
                                <th>{item.email}</th>
                                <th>{item.userName}</th>
                                <th>{item.fullName}</th>
                                <th>{item.department}</th>
                                <th>{item.position}</th>
                                <button>Edit</button>
                                <button>DElete</button>
                            </tr>
                        </table>
                    </div>



                )
            })}



        </div>
    )
}

export default UserPage;
