import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    color: '#000',
};

const colorSlice = createSlice({
    name: 'color',
    initialState,
    reducers: {
        change: (state, action) => {
            state.color = state.color === '#000' ? '#555' : '#7777';
        }
    },
});

export const { change } = colorSlice.actions;

export default colorSlice.reducer;
